#include <stdio.h>
float aceleracao(float vi, float vf, float ti, float tf){
	float am;
	
	am = (vf-vi)/(tf-ti);                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
	
	return am;
}

int main()
{
	float vf, vi, tf, ti, am;
	
	printf ("Velocidade inicial: ");
	scanf ("%f", &vi);
	
	printf ("Velocidade final: ");
	scanf ("%f", &vf);
	
	printf ("Tempo inicial: ");
	scanf ("%f", &ti);
	
	printf ("Tempo final: ");
	scanf ("%f", &tf);
	
	am = aceleracao(vi, vf, ti, tf);
	
	
	printf("Aceleracao escalar media e: %f", am);
}
